import{a as r,o as c,b as d,e,r as n,f as t,w as a,h as o,i as p,j as y}from"./entry.068a01bc.js";const m={props:{moreInfoHref:{type:String,required:!0}}},u={class:"p-8 m-8 shadow-md rounded-md flex flex-col space-y-4"},j={class:"font-bold text-3xl"},l={class:"grow"};function k(s,z,i,$,W,B){const w=p;return c(),d("div",u,[e("div",j,[n(s.$slots,"title")]),e("div",l,[n(s.$slots,"text")]),t(w,{href:i.moreInfoHref,class:"w-fit duration-200 p-4 border border-red-200 rounded-md hover:bg-red-300"},{default:a(()=>[o("Dowiedz si\u0119 wi\u0119cej")]),_:1},8,["href"])])}const h=r(m,[["render",k]]),g=""+globalThis.__publicAssetsURL("agnieszka1.jpg"),_={},f={class:"flex flex-col space-y-7"},b=y('<div class="grid grid-cols-2 place-items-center"><div><div class="text-4xl font-bold mb-4">Nasza misja i warto\u015Bci</div><div class="pl-4"> Centrum MediujeMY powsta\u0142o aby towarzyszy\u0107 Wam w trudnych sytuacjach sytuacji mo\u017Cna znale\u017A\u0107 wyj\u015Bcie dobre dla obu stron. Zar\xF3wno spory s\u0105dowe jak i pozas\u0105dowe mo\u017Cna rozwi\u0105zywa\u0107 w przyjaznej atmosferze wsp\xF3\u0142pracy, zaufania, poszanowania granic, uwzgl\u0119dniaj\u0105c emocje drugiej strony. Pracujemy opieraj\u0105c si\u0119 na fundamentalnych zasadach mediacji: dobrowolno\u015Bci, bezstronno\u015Bci, poufno\u015Bci i szacunku. Nasz zesp\xF3\u0142 to grupa prawnik\xF3w, socjolog\xF3w i pedagog\xF3w. Jako osoby do\u015Bwiadczone zawodowo i \u017Cyciowo pomo\u017Cemy Pa\u0144stwu w trudnych kwestiach zar\xF3wno \u017Cyciowych jak i zawodowych, wsz\u0119dzie tam gdzie pojawia si\u0119 konflikt, niezrozumienie, potrzeba g\u0142\u0119bszej wsp\xF3\u0142pracy czy poprawy komunikacji. Mo\u017Cemy Pa\u0144stwu pom\xF3c, poniewa\u017C jeste\u015Bmy tymi, kt\xF3rzy naprawd\u0119 POMAGAJ\u0104! Wierzymy w ide\u0119 mediacji i rozwi\u0105zywanie spor\xF3w w przyjaznej atmosferze zaufania i bezpiecze\u0144stwa. </div></div><div class="flex flex-col items-center"><img src="'+g+'" class="rounded-lg h-150"><div class="bg-slate-200 shadow-md rounded-md p-4"> Agnieszka Danieluk- pomys\u0142odawczyni i za\u0142o\u017Cycielka MediujeMy Centrum Mediacji, Komunikacji i Szkole\u0144.\xA0 \u2018\u2019Wierz\u0119 w ludzi,\xA0 i uwa\u017Cam, \u017Ce sami najlepiej wiedz\u0105 czego potrzebuj\u0105. Jako mediator towarzysz\u0119 im w drodze do budowania porozumienia poprzez s\u0142uchanie i wspieranie w tej drodze\u2019\u2019.</div></div></div><div class="text-red-500 text-3xl"> Zapewniamy Pa\u0144stwu bezp\u0142atne pierwsze spotkanie konsultacyjne, po kt\xF3rym mo\u017Cecie Pa\u0144stwo podj\u0105\u0107 decyzj\u0119 czy chcecie uczestniczy\u0107 w mediacji </div><div>slider opinii</div>',3),x={class:"flex flex-col"},v=e("div",null,"Jakie sprawy do mediacji?",-1),M=e("div",null," Mediacj\u0119 mo\u017Cemy prowadzi\u0107 we wszystkich sprawach, gdzie wyst\u0119puje potrzeba uregulowania kwestii prawnych czy faktycznych. B\u0119d\u0105 to mi\u0119dzy innymi sprawy cywilne, sprawy zakresu prawa rodzinnego w tym rozw\xF3d i separacja, o ustalenie opieki nad dzieckiem, o alimenty, sprawy z zakresu prawa gospodarczego w tym o zap\u0142at\u0119, spory pracownicze i wiele innych. Do trudnych sytuacji, konflikt\xF3w czy spor\xF3w dochodzi we wszystkich relacjach, spo\u0142ecze\u0144stwach i kulturach. Bardzo cz\u0119sto powstaj\u0105 one na skutek braku komunikacji. M\xF3wimy, ale nie s\u0142uchamy drugiej strony, a czasami wr\u0119cz nie chcemy jej s\u0142ucha\u0107. My jeste\u015Bmy tu po to, aby wys\u0142ucha\u0107 Pa\u0144stwa, us\u0142ysze\u0107 Wasz g\u0142os i przeprowadzi\u0107 Was przez proces budowania porozumienia z druga stron\u0105. Mediacja zak\u0142ada, \u017Ce strony przy pomocy mediatora, czyli neutralnej osoby trzeciej poprzez techniki komunikacyjne i odkrycie faktycznych potrzeb stron wypracuj\u0105 porozumienie co do zaistnia\u0142ej sytuacji. Niezale\u017Cnie od tego, czy Pa\u0144stwa sprawa jest ju\u017C na etapie postepowania sadowego, czy jeszcze nie trafi\u0142a do s\u0105du mediacje s\u0105 sposobem rozwi\u0105zania Pa\u0144stwa sytuacji. Oszcz\u0119dzicie pa\u0144stwo w ten spos\xF3b czas, pieni\u0105dze i rozwi\u0105\u017Cecie sytuacj\u0119 w komfortowych warunkach, oraz co jest warte podkre\u015Blenia we\u017Amiecie sp\xF3r we w\u0142asne r\u0119ce i zdecydujecie w jaki spos\xF3b chcecie go zako\u0144czy\u0107. Dzi\u0119ki mediacjom strony mog\u0105 zako\u0144czy\u0107 sp\xF3r ju\u017C na pierwszym posiedzeniu mediacyjnym Nasi mediatorzy pracuj\u0105 na terenie wojew\xF3dztwa lubelskiego, podsiadamy oddzia\u0142y w Lublinie, Na\u0142\u0119czowie oraz Bia\u0142ej Podlaskiej. ",-1),P={class:"grid grid-cols-2 mt-10"};function N(s,z){const i=h;return c(),d("div",f,[b,e("div",x,[v,M,e("div",P,[t(i,{moreInfoHref:"mediacjesadowe"},{title:a(()=>[o("Mediacje s\u0105dowe")]),text:a(()=>[o("W przypadku mediacji s\u0105dowych, s\u0105 one prowadzone na podstawie postanowienia s\u0105du wydanego z urz\u0119du lub na wiosek ka\u017Cdej ze stron. Zgodnie z kodeksem postepowanie cywilnego istnieje obowi\u0105zek pr\xF3by polubownego rozwi\u0105zania sporu przed z\u0142o\u017Ceniem pozwu. Mediacja s\u0105dowa charakteryzuje si\u0119 tym, \u017Ce ma ustawowo ustalony termin realizacji oraz okre\u015Blone koszty postepowania mediacyjnego. Po przeprowadzeniu mediacji , mediator zobowi\u0105zany jest przekaza\u0107 do s\u0105du protok\xF3\u0142 mediacji, a je\u017Celi zosta\u0142a zawarta ugoda, r\xF3wnie\u017C sam\u0105 ugod\u0119. Po zatwierdzeniu ugody przez s\u0105d ma ona tak\u0105 sam\u0105 moc prawn\u0105 jak wyrok. ")]),_:1}),t(i,{moreInfoHref:"mediacje"},{title:a(()=>[o("Mediacje pozas\u0105dowe")]),text:a(()=>[o(" W przypadku mediacji pozas\u0105dowych to strony same wybieraj\u0105 mediatora zg\u0142aszaj\u0105c si\u0119 do niego razem, lub jedna ze stron zg\u0142asza si\u0119 do mediatora i prosi o kontakt w swoim imieniu z drug\u0105 stron\u0105. Mediator nast\u0119pnie ustala ze stronami termin spotkanie i miejsce neutralne dla obydwu stron. Argumentem przemawiaj\u0105cym na korzy\u015B\u0107 mediacji pozas\u0105dowej jest szybszy termin zako\u0144czenia sprawy, porozumienie jest wypracowane w formie akceptowanej dla obu stron, ni\u017Csze koszty w por\xF3wnaniu do post\u0119powa\u0144 s\u0105dowych. Cz\u0119st\u0105 praktyk\u0105 jest to, i\u017C porozumienie mo\u017Cna wypracowa\u0107 ju\u017C na pierwszym spotkaniu. Podobnie jak w przypadku mediacji s\u0105dowych po zatwierdzeniu ugody przez s\u0105d zyskuje ona moc postanowienia s\u0105dowego, a w przypadkach dotycz\u0105cych roszcze\u0144 finansowych zostanie nadana klauzula wykonalno\u015Bci. ")]),_:1})])])])}const C=r(_,[["render",N]]);export{C as default};
